import{_ as s,c as n,o as a,a as l}from"./app.14877f81.js";const A=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"编程范式之命令式编程","slug":"编程范式之命令式编程","link":"#编程范式之命令式编程","children":[]}],"relativePath":"fe_advance/Vue/Vue3源码解析/01.基本概念/01.编程范式之命令式编程.md"}'),p={name:"fe_advance/Vue/Vue3源码解析/01.基本概念/01.编程范式之命令式编程.md"},o=l(`<h2 id="编程范式之命令式编程" tabindex="-1">编程范式之命令式编程 <a class="header-anchor" href="#编程范式之命令式编程" aria-hidden="true">#</a></h2><p>针对于目前的前端开发而言，主要存在两种 <strong>编程范式</strong></p><ol><li>命令式编程</li><li>声明式编程</li></ol><p>这两种 <strong>范式</strong> 一般是相对来说的。</p><p>首先来说什么叫做命令式。</p><p>具体栗子：</p><blockquote><p>李华的妈妈让他去买酱油</p><p>李华是这么做的：</p><ol><li>拿起钱</li><li>打开门</li><li>下了楼</li><li>去超市</li><li>用钱买酱油</li><li>回到家</li></ol></blockquote><p>以上的流程<s>详细</s>的描述了，李华在买酱油的过程中每一步都做了什么，那么这种：<strong>详细描述做事过程</strong> 的方式就可以被叫做 <strong>命令式</strong></p><p>那么如果把这样的方式放到具体的代码实现中，应该如何做呢？</p><p>来看这样一个事：</p><blockquote><p>在指定的div中展示 hello world</p></blockquote><div class="language-html line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">div</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">id</span><span style="color:#89DDFF;"> = </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">app</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">div</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">---------------------------</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#676E95;font-style:italic;">// 获取div</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> divG </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> document</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">querySelector</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">#app</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#676E95;font-style:italic;">// 为div 设置 innerHTML 为 hello world</span></span>
<span class="line"><span style="color:#A6ACCD;">    divG</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">innerHTML </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">hello world</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><p>代码虽然只有两行，但是 <strong>描述了完成这件事情所需要经历的过程</strong></p><p>那么我们要完成一个更复杂的事情，整个的过程就会变得更为复杂</p><p>比如：</p><blockquote><p>为指定的div的子元素div的子元素p标签，展示变量msg</p></blockquote><div class="language-javascript line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">javascript</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// 获取第一层div</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> divEle </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> document</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">querySelector</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">#app</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 获取第二层div</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> subDivEle </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> divEle</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">querySelector</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">div</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 获取第三层的p</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> subPEle </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> subDivEle</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">querySelector</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">p</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 定义变量msg</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> msg </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">hello world</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 为 p 设置 innerHTML 为 hello world</span></span>
<span class="line"><span style="color:#A6ACCD;">subPEle</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">innerHTML </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> msg</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br></div></div><p>总结：什么叫做命令式呢？</p><p>命令式就是：<strong>关注过程</strong>的一种编程范式，他描述完成一个功能的 <strong>详细逻辑与步骤</strong></p>`,19),e=[o];function t(r,c,i,D,y,F){return a(),n("div",null,e)}const d=s(p,[["render",t]]);export{A as __pageData,d as default};
